import React from 'react';
import { connect } from 'react-redux';

import {ReactComponent as Plus} from '../../assets/plus.svg'
import {ReactComponent as Minus} from '../../assets/minus.svg'

import { addToCart, removeFromCart } from '../../redux/cart/cart.actions';

import './basket-item.styles.scss';

import { ReactComponent as Cross} from '../../assets/x-mark.svg'

const BasketItem = ({ cartItem, removeFromCart, addToCart }) => {
    const { name, price, quantity, imageurl} = cartItem;
    return (
        <div className="basket-item">
            <div className="row align-items-center justify-content-around">
                <div className="col-2"><img src={`${imageurl}`} alt="basket-item"/></div>
                <div className="col-4 basket-item">{name}</div>
                <div className="col-2 basket-item">Â£{(price/100).toFixed(2)}p</div>
                <div className="col-2 basket-item">
                    <Minus onClick={() => removeFromCart(cartItem)}/>&bksp;
                    {quantity} 
                     <Plus onClick={() => addToCart(cartItem)}/>
                </div>
                <div className="col-2 basket-item remove-item"><Cross onClick={() => removeFromCart(cartItem)}></Cross></div>
            </div>
        </div>
    )
}

const mapDispatchToProps = dispatch => ({
    removeFromCart: item => dispatch(removeFromCart(item)),
    addToCart: item => dispatch(addToCart(item)),
})

export default connect(null, mapDispatchToProps)(BasketItem);
