import React from 'react';

import './signin.styles.scss';

class SignIn extends React.Component {
    constructor(){
        super();

        this.state = {
            email: '',
            password:''
        }
    }

    handleSubmit = event => {
        event.preventDefault();

        const { email, password } = this.state;

        axios({
            url: 'signin/user',
            method: 'post',
            data: {
                email,
                password
            }
        }).then(response => {
            <Redirect to="/" />
            console.log('successful')
        }).catch(error => {
            console.log('error');
            alert('There was an error, please try again.')
        });
    }

    handleChange = event => {
        const { name, value } = event.target;

        this.setState({ [name]: value });
    }

    render() {
        const { name, address1, address2, towncity, postcode, email, password, confirmPassword } = this.state;
        return (
            <div className="background">
                <div className="container">
                    <div className="row">
                        <div className="col-3"></div>
                        <div className="col-6 mt-5">
                        <Form className='registration-form' onSubmit={this.handleSubmit}>

                            <h1 className="register-heading mb-5">Register</h1>

                            <FormInput
                                label="Name"
                                type="text"
                                placeholder="Name"
                                controlId="name"
                                name="name"
                                value={name}
                                onChange={this.handleChange}
                                required
                            />
                            <FormInput
                                label="Address"
                                type="text"
                                placeholder="Address Line 1"
                                controlId="Address"
                                name="address1"
                                value={address1}
                                onChange={this.handleChange}
                                required
                            />
                            <div className="row justify-content-end register-button">
                            <CustomButton isRegister>
                                Register
                            </CustomButton>
                            </div>
                            </Form>
                        </div>
                        <div className="col-3"></div>
                    </div>
                </div>
            </div>
        )
    }
}

export default SignIn;
