import axios from 'axios'

export const RemoveFromCart = () => {
    const { _id } = cartItem

    axios({
        method: 'post',
        url: `items/${_id}/remove`,
        data: {
          item: cartItem
        }
      })
        .then(response => {
            console.log(response.data);
            removeFromCart(cartItem);
        })
        .catch(error => {
            console.log(error);
        });
}

export const AddToCart = () => {
    const {_id} = cartItem

    axios({
        method: 'post',
        url: `items/${_id}/add`,
        data: {
          item: cartItem
        }
      })
        .then(response => {
            console.log(response.data);
            addToCart(cartItem);
        })
        .catch(error => {
            console.log(error);
        });
}
