import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

import './header.styles.scss';
import Axios from 'axios';

const logoutRoute = () => {
    axios.get('logout')
        .then(response => {
            console.log(response);
        })
        .catch(error => {
            console.log(error);
        });
}

const Header = ({ currentUser }) => {
    return (
        <nav className="navbar sticky-top navbar-expand-lg navbar-light bg-transparent">
        <Link className="navbar-brand" to='/'>GroceryUs</Link>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
        </button>
        {
            currentUser ? (
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav">
                    <li className="nav-item">
                        <p className="nav-link">Hello, {currentUser.name}</p>
                    </li>
                    <li className="nav-item">
                        <div className="nav-link" onClick={logoutRoute}>Logout</div>
                    </li>
                    </ul>
                </div>
            ) : (
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav">
                    <li className="nav-item">
                        <Link className="nav-link" to="/register">Register</Link>
                    </li>
                    <li className="nav-item">
                        <Link className="nav-link" to="/login">Login</Link>
                    </li>
                    </ul>
                </div>
            )
        }
        </nav>
    )
}

const mapStateToProps = state => ({
    currentUser: state.user.currentUser
  });

export default connect(mapStateToProps)(Header);
