import React from 'react';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';
import { selectCurrentUser } from '../../redux/user/user.selectors';

import './checkout.styles.scss';

const CheckoutPage = ({ currentUser, ...props }) => {

    const { date, time } = props.location.state

    return (
        <div className="checkout-page">
            <div className="container">
                    <div className="row">
                    <div className="col-1"></div>
                    <div className="col-10 mt-5">
                        <div className="basket">
                            <h3 className="checkout-header">Checkout</h3>
                            <h4>Shipping details</h4>
                            <p className="shipping-details">{currentUser.name}</p>
                            <p className="shipping-details">{currentUser.address1}</p>
                            {currentUser.address2 ? <p className="shipping-details">{currentUser.address2}</p> : null}
                            <p className="shipping-details">{currentUser.towncity}</p>
                            <p className="shipping-details">{currentUser.postcode}</p>
                            <p className="shipping-details">{date}</p>
                            <p className="shipping-details">{time}</p>
                    </div>
                    <div className="col-1">
                    </div>
                </div>
            </div>
        </div>
    </div>
    )
}

const mapStateToProps = createStructuredSelector({
    currentUser: selectCurrentUser
});
export default connect(mapStateToProps)(CheckoutPage);
