import React, {useState} from 'react';
import { Form } from 'react-bootstrap'
import axios from 'axios';

import CustomButton from '../../components/custom-button/custom-button.component';
import FormInput from '../../components/form-input/form-input.component';

import { setCurrentUser } from '../../redux/user/user.actions';

import './register.styles.scss'

const RegisterPage = () => {

    const [userCredentials, setCredentials ] = useState(
        { 
            name: '', 
            address1: '', 
            address2: '', 
            towncity: '', 
            postcode: '', 
            email: '', 
            password: '',
            confirmPassword:''
    })

    const { name, address1, address2, towncity, postcode, email, password } = userCredentials;

    const handleSubmit = event => {
        event.preventDefault();

        axios({
            url: 'register/newuser',
            method: 'post',
            data: {
                name,
                address1,
                address2,
                towncity,
                postcode,
                email,
                password
            }
        }).then(response => {
            const user = response.data
            // this.props.history.push('/shop')
        }).catch(error => {
            console.log(error);
            alert('There was an error, please try again.')
        });
    }

    handleChange = event => {
        const { name, value } = event.target;

        console.log(name)
        console.log(value)

        setCredentials({ [name]: value });
    }

    return (
        <div className="background">
            <div className="container">
                <div className="row">
                    <div className="col-3"></div>
                    <div className="col-6 mt-5">
                    <Form className='registration-form' onSubmit={handleSubmit}>

                        <h1 className="register-heading mb-5">Register</h1>

                        <FormInput
                            label="Name"
                            type="text"
                            placeholder="Name"
                            controlId="name"
                            name="name"
                            value={name}
                            onChange={handleChange}
                            required
                        />
                        <FormInput
                            label="Address"
                            type="text"
                            placeholder="Address Line 1"
                            controlId="Address"
                            name="address1"
                            value={address1}
                            onChange={handleChange}
                            required
                        />
                        <FormInput
                            label=""
                            type="text"
                            placeholder="Address Line 2"
                            controlId="Address"
                            name="address2"
                            value={address2}
                            onChange={handleChange}
                        />
                        <FormInput
                            label="Town/City"
                            type="text"
                            placeholder="Town/City"
                            controlId="Address"
                            name="towncity"
                            value={towncity}
                            onChange={handleChange}
                            required
                        />
                        <FormInput
                            label="Postcode"
                            type="text"
                            placeholder="Postcode"
                            controlId="Address"
                            name="postcode"
                            value={postcode}
                            onChange={handleChange}
                            required
                        />
                        <FormInput
                            label="Email"
                            type="email"
                            placeholder="Email"
                            controlId="Email"
                            name="email"
                            value={email}
                            onChange={handleChange}
                            required
                        />
                        <FormInput
                            label="Password"
                            type="password"
                            placeholder="Password"
                            controlId="Password"
                            name="password"
                            value={password}
                            onChange={handleChange}
                            required
                        />
                        <FormInput
                            label="Confirm"
                            type="password"
                            placeholder="Confirm Password"
                            controlId="Password"
                            name="confirmpassword"
                            value={confirmPassword}
                            onChange={handleChange}
                            required
                        />

                    <div className="row justify-content-end register-button">
                    <CustomButton isRegister>
                        Register
                    </CustomButton>
                    </div>
                    </Form>
                    </div>
                    <div className="col-3"></div>
                </div>
            </div>
        </div>
    )
}


export default RegisterPage;
