        <Link to={{pathname: "/items",
            state:  {
                item: item
            }
        }}  className="col-3 justify-content-center item">
            <img src={item.imageurl} alt="item" height="100px"/>
            <h6 className="mt-2">{item.name}</h6>
            <h6>Â£{(item.price/100).toFixed(2)}p</h6>
        </Link>
    )
}

export default Item;
